import React, { Component } from "react";
import Checkbox from '@material-ui/core/Checkbox';
import DeleteOutlinedIcon from '@material-ui/icons/DeleteOutlined';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core';

class Elemento extends Component{
	state={
		acciones:false,
		edit:false
	}
	showme=e=>{
		this.setState({ acciones: !this.state.acciones });
		setTimeout(this.ocultaMe,2000);
	}
	editMe=e=>{
		this.setState({edit: true});
		console.log(this.props.id);
	}
	deleteMe=e=>{
		console.log(this.props.nombre);
	}
	ocultaMe=e=>{
		this.setState({acciones:false});
	}

	render(){
		return(
			<Grid container key={this.props.id} alignItems="center" spacing={1}>
				<Grid item>
					<Checkbox color="primary"/>
				</Grid>
				<Grid item>
					<span onClick={this.showme}>
						{ this.state.edit ?
							(<TextField
								id="texto"
								label={this.props.nombre}
								type="text"
								inputRef={e => (this.nuevoElemento =e)}

							/>):
						(this.props.nombre)}
						</span>
				</Grid>
					{ this.state.acciones ? (
						<Grid item>
							<EditOutlinedIcon onClick={ this.editMe } />
							<DeleteOutlinedIcon onClick={ this.deleteMe }/>
						</Grid>
				):("")}
			</Grid>
		)
	}
}

export default Elemento
